!function(l){l("#sidebarToggle, #sidebarToggleTop").on("click", function(e) {
    l("body").toggleClass("sidebar-toggled");
    l(".sidebar").toggleClass("toggled");
  
    // ðŸ”½ Add this block to toggle the logo size
    if (l(".sidebar").hasClass("toggled")) {
      l(".navbar-logo").addClass("minimized");
    } else {
      l(".navbar-logo").removeClass("minimized");
    }
  
    if (l(".sidebar").hasClass("toggled")) {
      l(".sidebar .collapse").collapse("hide");
    }
  });
  }(jQuery);